package examples;

public class Star2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++)
		{
			for(int m=5;m>i;m--)
			{
				System.out.print(" ");
			}
			for(int j=1;j<=4;j++)
				{
				
			
					System.out.print("*");
				}System.out.println();
		
		}

	}

}
